    private $type_configurable = Mage_Catalog_Model_Product_Type::TYPE_CONFIGURABLE;
    private $type_simple = Mage_Catalog_Model_Product_Type::TYPE_SIMPLE;

    private $status_enabled = Mage_Catalog_Model_Product_Status::STATUS_ENABLED;
    private $status_disabled = Mage_Catalog_Model_Product_Status::STATUS_DISABLED;

    private $visibility_both = Mage_Catalog_Model_Product_Visibility::VISIBILITY_BOTH;
    private $visibility_none = Mage_Catalog_Model_Product_Visibility::VISIBILITY_NOT_VISIBLE;

    /**
     * Run script
     *
     */
    public function run()
    {
        $i=1;
        $collection = Mage::getModel('catalog/product')->getCollection();
        $collection->addAttributeToSelect('status');
        $collection->addAttributeToFilter(array(
            array(
                'attribute' => 'sku',
                'in'        => $this->products,
                )
        ));
        foreach($collection as $product) {
            $storeIds = $product->getStoreIds();
            $storeId = array_pop($storeIds);

            $product->setStatus($this->status_enabled);
            /*if($storeId){
                $product->setStatus(false);
            }*/
            $product->setStoreId($storeId);
            if($product->getTypeId() == $this->type_simple){
                $product->setVisibility($this->visibility_none);
            }
            $product->save();
            echo ($i++) . '> '. $product->getSku() . ': type=(' . $product->getTypeId() . ') : status=(' . $product->getStatus() . ') : visibility=(' . $product->getVisibility() . ')' . "\n";
        }
    }
}

